import LinkEditor from "../LinkEditor/LinkEditor";
import './LinkEditorCard.css'
import { useState } from "react";

const AddLinkWidget = ({ setAddedLinks }) => {
    const [title, setTitle] = useState('')
    const [url, setUrl] = useState('')
    return(
        <form onSubmit={setAddedLinks()}>
        <label>Title:
        <input type="text" id="title" name="title" value={title} onChange={event => setTitle(event.target.value)}/>
        </label>
        <label>URL:
        <input type="text" id="url" name="url" value={url} onChange={event => setUrl(event.target.value)}/>
        </label>
        <input type="submit" value="Add New Link" />
        </form>
    )}

const LinkEditorCard = ({ links, title, setAddedLinks }) => {
  return (
    <div className="editor-card-wrapper">
      <div>{title}</div>
      {links.map((link) => (
          link.title && link.url &&
        <LinkEditor key={link.id} link={link} />
      ))}
      <AddLinkWidget setAddedLinks={setAddedLinks}/>
    </div>
  );
};

export default LinkEditorCard;
