// import ColorPicker from "../ColorPicker/ColorPicker"
import { useState } from "react";

const LinkPreview = ({ link, updateClicks }) => {
    const [className, setClassName] = useState("");
    const toggleRoundedCorners = () => {
        if (className === "rounded-corners") {
          setClassName("");
        }
        if (className === "") {
          setClassName("rounded-corners");
        }
      };
    const handleClick = () => {
        link.clicks += 1;
        updateClicks(link);
        toggleRoundedCorners();
    }
    // <ColorPicker />

    return (<div className={className}onClick={handleClick}>{link.title}</div>)
}

export default LinkPreview;
