import "./LinkPreviewCard.css";
import LinkPreview from "../LinkPreview/LinkPreview";
import { useState } from "react";

const LinkPreviewCard = ({ title, links, updateClicks }) => {
const [roundedCorners, setRoundedCorners] = useState("")

  return (
    <div className={"preview-card-wrapper"}>
      <div>{title}</div>
      {links.map((link) => (
        <div className={`link-preview ${roundedCorners}`} key={link.id} onClick={setRoundedCorners('rounded-corners')}>
          <LinkPreview link={link} updateClicks={updateClicks} setRoundedCorners={setRoundedCorners}/>
        </div>
      ))}
    </div>
  );
};

export default LinkPreviewCard;
