import "./App.css";
import LinkEditorCard from "./Components/LinkEditorCard/LinkEditorCard";
import LinkPreviewCard from "./Components/LinkPreviewCard/LinkPreviewCard";
import { useState, useEffect } from "react";

function App() {
  const [linksInBackend, setLinksInBackend] = useState([]);
  const [addedLinks, setAddedLinks] = useState([])
  const [presentationalLinks, setPresentationalLinks] = useState([])

  useEffect(() => {
    fetch("https://retoolapi.dev/T6xAX6/links")
      .then((response) => response.json())
      .then((result) => setLinksInBackend(result))
      .then(console.log(linksInBackend))
  }, []);

  const handleAddingLinks = () => {
    const addedLinksWithIds = addedLinks.map(link => { 
      return {link: link, title: link.title, clicks: 0, id: 'blah'}
    })
    presentationalLinks.concat(addedLinksWithIds)
  }

  if (addedLinks) {
    handleAddingLinks();
  }

  return (
    <div className="App">
      <LinkEditorCard
        className="link-editor-card"
        links={presentationalLinks}
        title={"Add New Links"}
        setAddedLinks={setAddedLinks}
      />
      <LinkPreviewCard
        className="link-preview-card"
        links={presentationalLinks}
        title={"Link Preview"}
      />
    </div>
  );
}

export default App;
